/*------------------------------------------------------------------------------------
  pageLoad events
------------------------------------------------------------------------------------*/
function pageLoad(){
  exec(queue.pageLoadEvents, 'pageLoadEvents');
}



/*------------------------------------------------------------------------------------
  Helper: Promotes #page element to the z-index top
------------------------------------------------------------------------------------*/
var page = {
  promote: function(){
    dlog_verbose('page.promote()');
    $('#page').addClass('-promoted');
  },
  demote: function(){
    dlog_verbose('page.demote()');
    $('#page').removeClass('-promoted');
  }
};



/*------------------------------------------------------------------------------------
  Get page
------------------------------------------------------------------------------------*/
function getPage(url, title, method, data, timeout) {
  
  // Log page request
  dlog('GET: ' + url + ' started...');
  var start = new Date().getTime();

  // Parse config
  var title = title || app.name;
  var method = method || 'GET';
  var data = data || [];

  // Hide the content of the page and show a loader
  $('#page').addClass('-loading');
  
  // Start the request
  $.ajax({
    type: method,
    url: url,
    data: data,
    timeout: 10000, // Wait for 10 seconds max
    success: function(data) {
      
      // Swap data
      $('#page').html(data);
      $('body').removeClass().addClass($('#relay').attr('bodyClass'));


      // Log success and waiting time
      var execTime = new Date().getTime() - start;
      dlog('GET: ' + url + ' success! (' + execTime + 'ms)');

      // Push history state and toggle related switches
      History.pushState(null, title, url);
      ux.url.isLoading = false;

      // Scroll to top
      $("html, body").scrollTop(0);

      
      $('#page').removeClass('-loading');

      // Run all pageLoad events
      pageLoad();

    },
    error: function(xhr, type) {
      
      // Log error and waiting time
      var execTime = new Date().getTime() - start;
      dlog('GET: ' + url + ' error! Reason: ' + type + '. (' + execTime + 'ms)');

      // Show an error message
      $('#pageLoad').html("Sorry, this is taking long. We will try one more time...").addClass('-show');

      // After a small pause, initiate a redirect to the target url
      setTimeout(function(){
        window.location = url;
      }, 500);

    }
  });
}



/*------------------------------------------------------------------------------------
  Check before leaving page (e.g., if a form is in progress to avoid losing data)
------------------------------------------------------------------------------------*/
var beforeUnload = {
  check: function(){
    dlog_verbose('beforeUnload.check: ');
    if ( this.target.height() === 0 ){ sectionInfo.open(); }
    else { sectionInfo.close(); }
  },
  exec: function(){
    exec(queue.globalUnloadEvents, 'GlobalUnload');
  }
};



